//Me devolvera todo el arreglo de objetos que tengo en data
import { Link } from "react-router-dom";
import { useState, useEffect } from "react";

export const ArticleList = ({ articles }) => {

    const [listaRestaurantes, setListaRestaurantes] = useState([])
    const obtenerRestaurantes = async () => {
        try {
            const response = await fetch("http://localhost:8000/clientes/restaurante/listar")
            const data = await response.json()
            setListaRestaurantes(data.restaurantes)
        } catch (error) {
            console.error("Error obteniendo restaurantes")
        }
    }
    useEffect(() => {
        obtenerRestaurantes()
    },)

return (
    <div className="article-list">
        {articles.map(article => (
            <div className="article-container" key={article.id}>
                <div className="img-container">
                    <img src={article.image} alt={article.title} />
                </div>
                <div className="article-body">
                    <h2>{article.title}</h2>
                    <p>{article.description}</p>
                    <div className="article-footer">
                        <span>{article.date} Â· </span>
                    </div>
                </div>
            </div>
        ))}
    </div>
)
};
export default ArticleList;
