function EstadoPedido(props) {
    const [pedidos, setPedidos] = useState([]);
  
    useEffect(() => {
      async function fetchPedidos() {
        const response = await fetch("http://localhost:8000/endpoints/obtener_pedidos");
        const data = await response.json();
        setPedidos(data);
      }
      fetchPedidos();
    }, []);
  
    return (
      <div className="container">
        <h1>Estado de pedidos</h1>
        <table className="table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Nombre</th>
              <th>Detalles</th>
              <th>Dirección</th>
              <th>Método de pago</th>
              <th>Código</th>
              <th>Total</th>
            </tr>
          </thead>
          <tbody>
            {pedidos.map((pedido) => (
              <tr key={pedido.pk}>
                <td>{pedido.pk}</td>
                <td>{pedido.fields.nombre}</td>
                <td>{pedido.fields.detalles}</td>
                <td>{pedido.fields.direccion}</td>
                <td>{pedido.fields.metodo}</td>
                <td>{pedido.fields.codigo}</td>
                <td>{pedido.fields.total}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
  
  export default EstadoPedido;
