import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../restaurant.css';

const Carrito = (props) => {
  const [total, setTotal] = useState(0);
  console.log("Total:", total);
  sessionStorage.setItem("total", total);

  useEffect(() => {
    // Calcula el total localmente
    const newTotal = props.cart.reduce((acc, item) => {
      return acc + parseFloat(item.precio) * item.quantity;
    }, 0);
    setTotal(newTotal);
    
    const detalles = props.cart.map(item => {
      return {
        id: item.id,
        nombre: item.name,
        cantidad: item.quantity
      }
    });
    sessionStorage.setItem('detalles', JSON.stringify(detalles));
  }, [props.cart]);

  return (
    <div className="carrito">
      <h1>Carrito</h1>
      <table>
        <thead>
          <tr>
            <th>Producto</th>
            <th>Cantidad</th>
            <th>Precio</th>
            <th>Total</th>
          </tr>
        </thead>
        <tbody>
          {props.cart.map((item) => (
            <tr key={item.id}>
              <td>{item.name}</td>
              <td>{item.quantity}</td>
              <td>{item.precio}</td>
              <td>{parseFloat(item.precio) * item.quantity}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <p>Total: {total}</p>
      <Link to="/confirmar_pedido">Confirmar pedido</Link>
    </div>
  );
};

export default Carrito;
