import TopNav from "../Global/TopNav";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

function Registrarentrega() {
  const [pedido, setPedido] = useState("");
  const [error, setError] = useState("");

  const FormRegistrar = function () {
    const [code, setCode] = useState("");

    const handleSubmit = async function (event) {
      const body = {
        code: code,
      };
      const response = await fetch(
        "http://localhost:8000/endpoints/registrarentrega",
        {
          method: "POST",
          body: JSON.stringify(body),
        }
      );

      const data = await response.json();
      if (data.error === "") {
        setError(data.error);
        setPedido(data.producto);
      } else {
        setError(data.error);
      }
    };

    return (
      <form>
        <label>Pin de pedido</label>
        <input
          type="text"
          id="id"
          value={code}
          onChange={(event) => setCode(event.target.value)}
        />
        <button type="button" onClick={handleSubmit}>
          Buscar Pedido
        </button>
      </form>
    );
  };

  const vacio = function () {
    console.log("Se hizo click");
    setPedido("");
    setError("");
    return <div>holla</div>;
  };

  const Tabla = function () {
    if (error === "") {
      if (pedido !== "") {
        return (
          <table>
            <tr>
              <th>ID</th>
              <th>Nombre</th>
              <th>Detalles</th>
              <th>Dirección</th>
              <th>Método</th>
              <th>Total</th>
              <th>Estado</th>
              <th></th>
            </tr>
            <tr>
              <td>{pedido.code_v}</td>
              <td>{pedido.desc}</td>
              <td>{pedido.desc}</td>
              <td>{pedido.code_v}</td>
              <td>{pedido.code_v}</td>
              <td>{pedido.code_v}</td>
              <td>{pedido.code_v}</td>
              <td>
                <button onClick={cambiarEstado}>Confirmar Entrega</button>
              </td>
            </tr>
          </table>
        );
      } else {
        return <div>No hay pedidos seleccionados</div>;
      }
    } else {
      return <div>{error}</div>;
    }
  };

  const navigate = useNavigate();

  const cambiarEstado = async function () {
    const body = {
      id: pedido.code,
      estado: "E",
    };
    const response = await fetch(
      "http://localhost:8000/endpoints/cambiarEstado_Pedido",
      {
        method: "POST",
        body: JSON.stringify(body),
      }
    );
    const data = await response.json();
    if (data.error === undefined) {
      navigate("/");
    } else {
      console.log("Hubo un error");
    }
  };

  return (
    <div>
      <TopNav />
      <h1>Registrar Entrega</h1>
      <FormRegistrar />
      <Tabla />
    </div>
  );
}

export default Registrarentrega;
