const Restaurant = () => {
  const [menu, setMenu] = useState([]);
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);

  useEffect(() => {
    fetch('http://localhost:8000/endpoints/platosgenericos')
      .then(response => response.json())
      .then(data => {
        setMenu(data.platos_genericos);
      })
      .catch(error => {
        console.error('Error fetching menu:', error);
      });
  }, []);

  const handleAddToCart = (item) => {
    const foundItem = cart.find(i => i.nombre === item.nombre);
    if (foundItem) {
      foundItem.cantidad += 1;
      setCart([...cart]);
    } else {
      setCart([...cart, { ...item, cantidad: 1 }]);
    }
    
    // Envía la solicitud POST para obtener el total del carrito
    fetch('http://localhost:8000/endpoints/total', {
      method: 'POST',
      body: JSON.stringify(cart),
    })
      .then(response => response.json())
      .then(data => {
        setTotal(data.total);
      })
      .catch(error => {
        console.error('Error calculating total:', error);
      });
  };

  const handleRemoveFromCart = (item) => {
    const foundItem = cart.find(i => i.nombre === item.nombre);
    if (foundItem) {
      foundItem.cantidad -= 1;
      if (foundItem.cantidad === 0) {
        setCart(cart.filter(i => i.nombre !== item.nombre));
      } else {
        setCart([...cart]);
      }
      
      // Envía la solicitud POST para obtener el total del carrito
      fetch('http://localhost:8000/endpoints/total', {
        method: 'POST',
        body: JSON.stringify(cart),
      })
        .then(response => response.json())
        .then(data => {
          setTotal(data.total);
        })
        .catch(error => {
          console.error('Error calculating total:', error);
        });
    }
  };

  return (
    <div className="restaurant-container">
      <div className="menu-container">
        <h2>Menú</h2>
        <div className="menu">
          {menu.map(item => (
            <div key={item.id} className="item">
              <div className="item-info">
                <h3>{item.nombre}</h3>
                <p>{item.descripcion}</p>
                <p>Precio: ${item.precio}</p>
              </div>
              <button className="add-button" onClick={() => handleAddToCart(item)}>Agregar</button>
            </div>
          ))}
        </div>
      </div>
      <Carrito cart={cart} total={total} handleRemoveFromCart={handleRemoveFromCart} />
    </div>
  );
};

export default Restaurant;
