import React, { useState, useEffect } from 'react';
import Carrito from './components/carrito.jsx';
import {useNavigate} from 'react-router-dom';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './restaurant.css';

const Restaurant = () => {
  //const history = useHistory();
  const [platos, setPlatos] = useState([]);
  const [carrito, setCarrito] = useState([]);
  const navigate = useNavigate();

  React.useEffect(() => {
    const fetchData = async () => {
      const result = await axios.get('http://localhost:8000/endpoints/platosgenericos');
      setPlatos(result.data);
    };
    fetchData();
  }, []);

  const addToCart = (plato) => {
    const exist = carrito.find((x) => x.id === plato.id);
    if (exist) {
      setCarrito(
        carrito.map((x) =>
          x.id === plato.id ? { ...exist, cantidad: exist.cantidad + 1 } : x
        )
      );
    } else {
      setCarrito([...carrito, { ...plato, cantidad: 1 }]);
    }
  };

  const removeFromCart = (plato) => {
    const exist = carrito.find((x) => x.id === plato.id);
    if (exist.cantidad === 1) {
      setCarrito(carrito.filter((x) => x.id !== plato.id));
    } else {
      setCarrito(
        carrito.map((x) =>
          x.id === plato.id ? { ...exist, cantidad: exist.cantidad - 1 } : x
        )
      );
    }
  };

  const getCartCount = () => {
    return carrito.reduce((acc, { cantidad }) => acc + cantidad, 0);
  };

  const getCartTotal = () => {
    return carrito.reduce((total, { precio, cantidad }) => total + precio * cantidad, 0);
  };

  const hacerPedido = () => {
    navigate('/registrar_pedido', { platos: carrito, total: getCartTotal() });
  };

  return (
    <div className="restaurant-container">
      <div className="menu-container">
        <h1>Men√∫</h1>
        <div className="platos-container">
          {platos.map((plato) => (
            <div className="plato-container" key={plato.id}>
              <h2>{plato.nombre}</h2>
              <p>{plato.descripcion}</p>
              <p>${plato.precio}</p>
              <button className="add-to-cart-btn" onClick={() => addToCart(plato)}>
                Agregar al carrito
              </button>
            </div>
          ))}
        </div>
      </div>
      <div className="cart-container">
        <Carrito cartItems={carrito} removeFromCart={removeFromCart} hacerPedido={hacerPedido} />
      </div>
    </div>
  );
};

export default Restaurant;

