function EstadoPedido() {
  const [pedidos, setPedidos] = useState([]);

  useEffect(() => {
    async function fetchPedidos() {
      const response = await fetch('http://localhost:8000/endpoints/pedidos');
      const data = await response.json();
      setPedidos(data.pedidos);
    }
    fetchPedidos();
  }, []);

  const cambiarEstadoPedido = (id, nuevoEstado) => {
    fetch('http://127.0.0.1:8000/endpoints/cambiarEstado', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({id: id, estado: nuevoEstado})
    })
    .then(response => {
      console.log(response);
      setPedidos(pedidos.map(pedido => {
        if (pedido.id === id) {
          return {
            ...pedido,
            estado: nuevoEstado
          };
        }
        return pedido;
      }));
    })
    .catch(error => {
      console.log(error);
    });
  }
  
  return (
    <div>
      <TopNav category ={4}/>
      <center><h1>Estado de pedidos</h1></center>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nombre</th>
            <th>Detalles</th>
            <th>Dirección</th>
            <th>Método de pago</th>
            <th>Código</th>
            <th>Estado</th>
            <th>Total</th>
            <th>Cambiar Estado</th>
          </tr>
        </thead>
        <tbody>
          {pedidos.map(pedido => (
            <tr key={pedido.id}>
              <td>{pedido.id}</td>
              <td>{pedido.nombre}</td>
              <td>{pedido.detalles}</td>
              <td>{pedido.direccion}</td>
              <td>{pedido.metodo}</td>
              <td>{pedido.codigo}</td>
              <td>{pedido.estado}</td>
              <td>{pedido.total}</td>
              <td>
                <button
                  onClick={() => {
                    cambiarEstadoPedido(pedido.id, 'T');
                  }}
                >
                  Terminado
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default EstadoPedido;
