import axios from 'axios';

function RegistrarPedido(props) {
  const [nombre, setNombre] = useState("");
  const [direccion, setDireccion] = useState('');
  const [detalles, setDetalles] = useState('');
  const [metodoPago, setMetodoPago] = useState('');

  const handleSubmit = event => {
    event.preventDefault();
    const pedido = {
      id_pedido: props.match.params.id_pedido,
      nombre,
      direccion,
      detalles,
      metodo_pago: metodoPago
    };
    axios.post('/registrar_pedido', pedido)
      .then(response => {
        console.log(response);
      })
      .catch(error => {
        console.log(error);
      });
  };

  return (
    <div>
      <h1>Registrar Pedido</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Nombre:
          <input type="text" value={nombre} onChange={event => setNombre(event.target.value)} />
        </label>
        <br />
        <label>
          Dirección:
          <input type="text" value={direccion} onChange={event => setDireccion(event.target.value)} />
        </label>
        <br />
        <label>
          Detalles:
          <textarea value={detalles} onChange={event => setDetalles(event.target.value)} />
        </label>
        <br />
        <label>
          Método de pago:
          <select value={metodoPago} onChange={event => setMetodoPago(event.target.value)}>
            <option value="tarjeta">Tarjeta de crédito</option>
            <option value="efectivo">Efectivo</option>
          </select>
        </label>
        <br />
        <button type="submit">Registrar Pedido</button>
      </form>
    </div>
  );
}

export default RegistrarPedido;
