function EstadoPedido() {
    const [pedidos, setPedidos] = useState([]);
  
    useEffect(() => {
      async function fetchPedidos() {
        const response = await fetch('http://localhost:8000/endpoints/pedidos');
        const data = await response.json();
        setPedidos(data.pedidos);
      }
      fetchPedidos();
    }, []);
  
    function formatearDetalles(detalles) {
        if (!Array.isArray(detalles)) {
          return null;
        }
      
        return detalles.map((detalle) => (
          <div key={detalle.id}>
            <p>Producto: {detalle.producto}</p>
            <p>Cantidad: {detalle.cantidad}</p>
            <p>Precio: {detalle.precio}</p>
          </div>
        ));
      }
  
    return (
      <div>
        <TopNav category={4} />
        <center><h1>Estado de pedidos</h1></center>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Nombre</th>
              <th>Detalles</th>
              <th>Dirección</th>
              <th>Método de pago</th>
              <th>Código</th>
              <th>Estado</th>
              <th>Total</th>
            </tr>
          </thead>
          <tbody>
            {pedidos.map(pedido => (
              <tr key={pedido.id}>
                <td>{pedido.id}</td>
                <td>{pedido.nombre}</td>
                <td>{formatearDetalles(pedido.detalles)}</td>
                <td>{pedido.direccion}</td>
                <td>{pedido.metodo}</td>
                <td>{pedido.codigo}</td>
                <td>{pedido.estado}</td>
                <td>{pedido.total}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
  
  export default EstadoPedido;
