import React, { useEffect, useState } from 'react';
import axios from 'axios';
import TopNav from '../Global/TopNav';
import { Link } from 'react-router-dom';

/*
const verPedido = () => {
    const [pedidos, setPedidos] = useState([
        { id: 1, nombre: "Renzo", plato: "fideos", direccion: "Pueblo Libre", estado: "en preparación", codigo: "ABC123" },
        { id: 2, nombre: "Juan", plato: "pizza", direccion: "Jesus Maria", estado: "en preparación", codigo: "DEF456" },
        { id: 3, nombre: "Roberto", plato: "ensalada", direccion: "Los Olivos", estado: "en preparación", codigo: "GHI789" }
    ]);

    return (
        <div>
            <center><h1>Estado del Pedido</h1></center>
            <table>
            <thead>
            <tr>
                <th>ID</th>
                <th>Nombre</th>
                <th>Plato</th>
                <th>Direccion</th>
                <th>Estado</th>
                <th>Código</th>
            </tr>
            </thead>
            <tbody>
                {pedidos.map(pedido => (
                    <tr key={pedido.id}>
                        <td>{pedido.id}</td>
                        <td>{pedido.nombre}</td>
                        <td>{pedido.plato}</td>
                        <td>{pedido.direccion}</td>
                        <td>{pedido.estado}</td>
                        <td>{pedido.codigo}</td>
                    </tr>
                ))}
            </tbody>
            </table>
        </div>
    );
};
*/
function EstadoPedido() {
  const [pedidos, setPedidos] = useState([]);

  useEffect(() => {
    async function fetchPedidos() {
      const response = await fetch('http://localhost:8000/endpoints/pedidos');
      const data = await response.json();
      setPedidos(data.pedidos);
    }
    fetchPedidos();
  }, []);
  
  cambiarEstadoPedido = (id, nuevoEstado) => {
    this.setState(prevState => ({
      pedidos: prevState.pedidos.map(pedido => {
        if (pedido.id === id) {
          axios.post('http://127.0.0.1:8000/endpoints/cambiarEstado', {id: id, estado: nuevoEstado}) // Aquí se realiza la llamada a axios
          .then(response => {
            console.log(response.data); // manejo de la respuesta 
          })
          .catch(error => {
            console.log(error); // manejo de errores 
          });
          return {
            ...pedido,
            estado: nuevoEstado
          };
        }
        return pedido;
      })
    }));
  }

  return (
    <div>
      <TopNav category ={4}/>
      <center><h1>Estado de pedidos</h1></center>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nombre</th>
            <th>Detalles</th>
            <th>Dirección</th>
            <th>Método de pago</th>
            <th>Código</th>
            <th>Estado</th>
            <th>Total</th>
          </tr>
        </thead>
        <tbody>
          {pedidos.map(pedido => (
            <tr key={pedido.id}>
              <td>{pedido.id}</td>
              <td>{pedido.nombre}</td>
              <td>{pedido.detalles}</td>
              <td>{pedido.direccion}</td>
              <td>{pedido.metodo}</td>
              <td>{pedido.codigo}</td>
              <td>{pedido.estado}</td>
              <td>{pedido.total}</td>
              <td><button
                onClick={() => {  //Cambiar el estado del pedido al hacer click en el botón
                  this.cambiarEstadoPedido(pedido.id, 'En preparación');
                }}
              >
                Cambiar estado a "En preparación"
              </button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default EstadoPedido;
