function EstadoPedido() {
    const [pedidos, setPedidos] = useState([]);
  
    useEffect(() => {
      async function fetchPedidos() {
        const response = await fetch('http://localhost:8000/endpoints/pedidos');
        const data = await response.json();
        setPedidos(data.pedidos);
      }
      fetchPedidos();
    }, []);
  
    function detallesToString(detallesObj) {
        if (Array.isArray(detallesObj)) { // agregar esta verificación
          return detallesObj.map((detalle, index) => (
            <li key={index}>
              {detalle.cantidad} x {detalle.plato.nombre} (${detalle.plato.precio})
            </li>
          ));
        } else {
          return ''; // Devuelve una cadena vacía si no es un arreglo
        }
      }
    
    return (
      <div>
        <TopNav category ={4}/>
        <center><h1>Estado de pedidos</h1></center>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Nombre</th>
              <th>Detalles</th>
              <th>Dirección</th>
              <th>Método de pago</th>
              <th>Código</th>
              <th>Estado</th>
              <th>Total</th>
            </tr>
          </thead>
          <tbody>
            {pedidos.map(pedido => (
              <tr key={pedido.id}>
                <td>{pedido.id}</td>
                <td>{pedido.nombre}</td>
                <td>{detallesToString(JSON.parse(pedido.detalles))}</td>
                <td>{pedido.direccion}</td>
                <td>{pedido.metodo}</td>
                <td>{pedido.codigo}</td>
                <td>{pedido.estado}</td>
                <td>{pedido.total}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
  
  export default EstadoPedido;
