import React, { useState } from "react";
import { Link } from 'react-router-dom';
import './restaurant.css'

function RegisterOrderForm(props) {
  //const total = props.location.state.total;
  //console.log(props);
  //console.log(props.location);
  //console.log(props.location.state);
  //const total = props.location.state ? props.location.state.total : 0;
  console.log(props.total);
  const total = props.total;
  const [names, setNames] = useState("");
  const [address, setAddress] = useState("");
  const [addressDetails, setAddressDetails] = useState("");
  const [paymentMethod, setPaymentMethod] = useState("");
    
  const handleSubmit = async (event) => {
    event.preventDefault();
    const response = await fetch("http://localhost:8000/endpoints/registrar_pedido", {
      method: "POST",
      headers: {
        "Content-Type": "application/x-www-form-urlencoded"
      },
      body: new URLSearchParams({
        names,
        address,
        addressDetails,
        paymentMethod
      })
    });
    const data = await response.json();
    console.log(data.message); // o data.error si ocurre un error
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <center><h1>Registrar Pedido</h1></center>
      </div>
      <div className="caja">
        <div>
          <label htmlFor="names">Nombres__:</label>
          <input
            type="text"
            id="names"
            value={names}
            onChange={(event) => setNames(event.target.value)}
          />
        </div>
      </div>

      <div className="caja">
        <div>
          <label htmlFor="address">Dirección_:</label>
          <input
            type="text"
            id="address"
            value={address}
            onChange={(event) => setAddress(event.target.value)}
          />
        </div>
      
        <div>
          <label htmlFor="addressDetails">Detalles__:</label>
          <input
            type="text"
            id="addressDetails"
            value={addressDetails}
            onChange={(event) => setAddressDetails(event.target.value)}
          />
        </div>
      </div>


      <div className="caja">
        <label htmlFor="paymentMethod">Método de pago:</label>
        <select
          id="paymentMethod"
          value={paymentMethod}
          onChange={(event) => setPaymentMethod(event.target.value)}
        >
          <option value="">Seleccione un método de pago</option>
          <option value="cash">Efectivo</option>
          <option value="card">Tarjeta</option>
        </select>
      </div>

      <center><div className="boton_registrar">
          <button type="submit">Registrar pedido</button>
      </div></center>
    </form>
  );
}

export default RegisterOrderForm;
