/*El siguiente req se hizo con el apoyo de este video de YT: https://youtu.be/JY7eV4VMeV0*/
import './Req3.css'
import data from './data.js';
import ArticleList from './ArticleList';
import ButtonList from './ButtonList';
import { useState, useEffect } from 'react';
import axios from 'axios';

const Req3 = () => {

    const [categorias, setCategorias] = useState([]);

    const peticionGet = async () => {
        await axios.get("http://localhost:8000/endpoints/restaurante/listar")
            .then(response => {
                console.log(response.Restaurante)
                setCategorias(response.Restaurante);
                console.log(categorias)
            }).catch(error => {
                console.log(error);
            })
    }

    /*Renderiza cada que se ejecute la petición GET*/
    useEffect(() => {
        peticionGet();
    }, [])

    //En Restaurante cada objeto tiene su categoria, asi que la iteraremos con Math y guardaremos la categoría en un arreglo
    const allCategories = ['All', ...new Set(Restaurante.map(article => article.category))]

    //Usaremos 2 estados en esta aplicacion
    const [categories, setcategories] = useState(allCategories)
    const [articles, setArticles] = useState(Restaurante)

    const filterCategory = (category) => {
        if (category === 'All') {
            setArticles(Restaurante)
            return
        }
        //Filtra todo lo que es article.category de la categoria elegida por el usuario
        const filteredRestaurante = Restaurante.filter(article => article.category === category);
        setArticles(filteredRestaurante)
    }



    return (
        <>
            <ButtonList categories={categories} filterCategory={filterCategory} />
            <ArticleList articles={articles} />
        </>
    );
};
export default Req3;
