import React, { useState, useEffect } from 'react';
import Carrito from './components/carrito.jsx';
import {useNavigate} from 'react-router-dom';
import { Link } from 'react-router-dom';
import './restaurant.css';

const Restaurant = () => {
  const [platos, setPlatos] = useState([]);
  const [carrito, setCarrito] = useState([]);
  const [total, setTotal] = useState(0);

  const actualizarPlatos = (nuevosPlatos) => {
    setPlatos(nuevosPlatos);
  };

  const agregarAlCarrito = (plato) => {
    const nuevoCarrito = [...carrito];
    const indexPlato = nuevoCarrito.findIndex((p) => p.id === plato.id);
    if (indexPlato >= 0) {
      nuevoCarrito[indexPlato].cantidad += 1;
    } else {
      nuevoCarrito.push({ ...plato, cantidad: 1 });
    }
    setCarrito(nuevoCarrito);
    setTotal(total + plato.precio);
  };

  const quitarDelCarrito = (plato) => {
    const nuevoCarrito = [...carrito];
    const indexPlato = nuevoCarrito.findIndex((p) => p.id === plato.id);
    if (indexPlato >= 0) {
      if (nuevoCarrito[indexPlato].cantidad === 1) {
        nuevoCarrito.splice(indexPlato, 1);
      } else {
        nuevoCarrito[indexPlato].cantidad -= 1;
      }
      setCarrito(nuevoCarrito);
      setTotal(total - plato.precio);
    }
  };

  return (
    <div className="restaurant-container">
      <div className="restaurant-menu">
        <h1>Men√∫</h1>
        <ListaPlatos platos={platos} agregarAlCarrito={agregarAlCarrito} />
      </div>
      <div className="restaurant-order">
        <h1>Carrito de Compras</h1>
        <Carrito
          carrito={carrito}
          total={total}
          quitarDelCarrito={quitarDelCarrito}
        />
      </div>
    </div>
  );
};

export default Restaurant;

