//Me devolvera todo el arreglo de objetos que tengo en data
import { Link } from "react-router-dom";
import axios from "axios";
import { useState, useEffect } from "react";

export const ArticleList = ({ articles }) => {

    const [listaCategorias, setListaCategorias] = useState([])
    const obtenerCategoriaAsynAwait = async function () {
        try {
            const response = await fetch("http://localhost:8000/endpoints/restaurante/listar")
            const data = await response.json()
            setListaCategorias(data)
        }catch(error){
            console.error("Error")
        }
}


const [categorias, setCategorias] = useState([]);
const peticionGet = async () => {
    await axios.get("http://localhost:8000/endpoints/restaurante/listar")
        .then(response => {
            console.log(response.data)
            setCategorias(response.data);
            console.log(categorias)
        }).catch(error => {
            console.log(error);
        })
}
/*Renderiza cada que se ejecute la petición GET*/
useEffect(() => {
    peticionGet();
}, [])


return (
    <div className="article-list">
        {articles.map(article => (
            <div className="article-container" key={article.id}>
                <div className="img-container">
                    <img src={article.image} alt={article.title} />
                </div>
                <div className="article-body">
                    <h2>{article.title}</h2>
                    <p>{article.description}</p>
                    <div className="article-footer">
                        <span>{article.date} · </span>
                    </div>
                </div>
            </div>
        ))}
    </div>
)
};
export default ArticleList;
