const Restaurant = ({ plato }) => {
  const [status] = useState(plato.status);

  const estados = ["Pendiente", "En preparacion", "Terminado"]

  return (
        <tr>
            <td>{plato.id}</td>
            <td>{plato.nombre}</td>
            <td>{plato.restaurante}</td>
            <td>{plato.plato}</td>
            <td>{plato.direccion}</td>
            <td>{plato.estado}</td>
            <td>{plato.codigo}</td>
        </tr>
  );
};


function Estados(){
  const [listaPedidos, setListaPedidos] = useState([])

  const obtenerPedidos = async function(){
      try{
          const response = await fetch("http://localhost:8000/endpoints/platosgenericos")
          const data = await response.json()
          console.log(data.arreglo);
          setListaPedidos(data.arreglo)
      }
      catch(error){
          console.error("Hubo un error obteniendo los pedidos")
      }
  }

  useEffect(function(){
      obtenerPedidos()
  }, [])

  let filas = []
  for(let i = 0; i < listaPedidos.length; i++){
      let pedido = listaPedidos[i]
      let elemento = <Pedido key={pedido.id} pedido={pedido} />
      filas.push(elemento)
  }

  return <div>
      <TopNav category ={4}/>
      <br />
      <center><h1>Estado del Pedido</h1></center>
      <table>
        <tr>
          <th><h3>ID</h3></th>
          <th><h3>Nombre</h3></th>
          <th><h3>Restaurante</h3></th>
          <th><h3>Plato</h3></th>
          <th><h3>Direccion</h3></th>
          <th><h3>Estado</h3></th>
          <th><h3>Codigo</h3></th>
        </tr>
        {filas}
      </table>
  </div>
}

export default Restaurant
