function Restaurant(){
  const [pedido, setPlato] = useState("")
  const [error, setError] = useState("")
  const FormPlatos = function(){
    const [code, setCode] = useState('');
    const handleSubmit = async function(event){
      const body = {
        code : code
      }
      const response = await fetch('http://localhost:8000/endpoints/platosgenericos', {method : "POST",
      body : JSON.stringify(body)})

      const data = await response.json()
      if (data.error ==="") {
        setError(data.error)
        setPlato(data.producto)
      }
      else{
        setError(data.error)
      }
    }
     
    return <form>
      <label>Pin de pedido</label>
      <input type="text" id="code" value={code} onChange={(event) => setCode(event.target.value)}/>
      <button type="button" onClick={handleSubmit}>Buscar Pedido</button>
    </form>
  }
  const vacio = function(){
    console.log('Se hizo click')
    setPlato('')
    setError('')
    return <div>holla</div>
  }
  const Tabla = function(){
    if (error === "") {
      if (pedido!== "") {
        return <table>
        <tr>
          <th>Cod</th>
          <th>Detalle</th>
          <th>Pin de verificaci√≥n</th>
        </tr>
        <tr>
          <th>{pedido.code}</th>
          <th>{pedido.desc}</th>
          <th>{pedido.code_v}</th>
          <th><button type="submit" onClick={vacio}>Confirmar la entrega</button></th>
        </tr>
      </table>
      }
    }
    else {
      return <h2>{error}</h2>
    }
  }
  return <div>
    <TopNav category={3}/>
    <br />
    <h1>Registrar una entrega de pedido</h1>
    <FormPlatos/>
    <Tabla/>
  </div>
}

export default Restaurant
