function EstadoPedido() {
    const [pedidos, setPedidos] = useState([]);
  
    useEffect(() => {
      async function fetchPedidos() {
        const response = await fetch('http://localhost:8000/endpoints/pedidos');
        const data = await response.json();
        setPedidos(data.pedidos);
      }
      fetchPedidos();
    }, []);

    async function getPedidos() {
        const codigo_pedido = document.getElementById('codigo_pedido').value;
        if (codigo_pedido) {
          const response = await fetch(`/api/buscar_pedido_por_codigo/?codigo_pedido=${codigo_pedido}`);
          const data = await response.json();
          if (response.ok) {
            setPedidos(data.pedidos);
          } else {
            console.error(`Error ${response.status}: ${data.error}`);
          }
        } else {
          const response = await fetch('/api/pedidos/');
          const data = await response.json();
          if (response.ok) {
            setPedidos(data.pedidos);
          } else {
            console.error(`Error ${response.status}: ${data.error}`);
          }
        }
      }
    
  
    return (
      <div>
        <TopNav category ={4}/>
        <center><h1>Estado de pedidos</h1></center>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Nombre</th>
              <th>Detalles</th>
              <th>Dirección</th>
              <th>Método de pago</th>
              <th>Código</th>
              <th>Estado</th>
              <th>Total</th>
            </tr>
          </thead>
          <tbody>
            {pedidos.map(pedido => (
              <tr key={pedido.id}>
                <td>{pedido.id}</td>
                <td>{pedido.nombre}</td>
                <td>{pedido.detalles}</td>
                <td>{pedido.direccion}</td>
                <td>{pedido.metodo}</td>
                <td>{pedido.codigo}</td>
                <td>{pedido.estado}</td>
                <td>{pedido.total}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
  
  export default EstadoPedido;
