import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../restaurant.css'

const Carrito = (props) => {
  const [total, setTotal] = useState(0);

  // Observar cambios en el carrito y actualizar el valor total
  useEffect(() => {
    let newTotal = 0;
    props.cart.forEach(item => {
      newTotal += item.price * item.quantity;
    });
    setTotal(newTotal);
  }, [props.cart]);

  const handleAddToCart = (item) => {
    const foundItem = props.cart.find(i => i.name === item.name);
    if (foundItem) {
      foundItem.quantity += 1;
      props.setCart([...props.cart]);
    } else {
      props.setCart([...props.cart, { ...item, quantity: 1 }]);
    }
  };

  const handleRemoveFromCart = (item) => {
    const foundItem = props.cart.find(i => i.name === item.name);
    if (foundItem) {
      if (foundItem.quantity === 1) {
        props.setCart(props.cart.filter(i => i.name !== item.name));
      } else {
        foundItem.quantity -= 1;
        props.setCart([...props.cart]);
      }
    }
  };

  return (
    <div className='carrito'>
      <center><h1>Carrito</h1></center>
      <table>
        <thead>
          <tr>
            <th>Comida</th>
            <th>Cantidad</th>
            <th>Precio</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {props.cart.map(item => (
            <tr key={item.name}>
              <td>{item.name}</td>
              <td>{item.quantity}</td>
              <td>{item.price * item.quantity}</td>
              <td>
                <button onClick={() => handleRemoveFromCart(item)}>-</button>
                <button onClick={() => handleAddToCart(item)}>+</button>
              </td>
            </tr>
          ))}
          <tr>
            <td colSpan={3}>Total</td>
            <td>{total}</td>
          </tr>
        </tbody>
      </table>
      <Link to="/Restaurantes/registro_pedido">
        <center><button>Realizar Pedido</button></center>
      </Link>
    </div>
  );
};

export default Carrito;
