const EstadoPedido = () => {
    const [codigoPedido, setCodigoPedido] = useState('');
    const [pedidos, setPedidos] = useState([]);
  
    const buscarPedido = () => {
      axios.get(`http://localhost:8000/endpoints/buscar_pedido_por_codigo=${codigoPedido}`)
        .then(res => {
          setPedidos(res.data.pedidos);
        })
        .catch(err => console.log(err));
    };
  
    return (
      <div>
        <h2>Estado de Pedido</h2>
        <div>
          <label>Código de pedido:</label>
          <input type="text" value={codigoPedido} onChange={e => setCodigoPedido(e.target.value)} />
          <button onClick={buscarPedido}>Ver Estado</button>
        </div>
        {pedidos.length > 0 && 
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Nombre</th>
                <th>Detalles</th>
                <th>Dirección</th>
                <th>Método</th>
                <th>Código</th>
                <th>Total</th>
                <th>Estado</th>
              </tr>
            </thead>
            <tbody>
              {pedidos.map(pedido => (
                <tr key={pedido.id}>
                  <td>{pedido.id}</td>
                  <td>{pedido.nombre}</td>
                  <td>{pedido.detalles}</td>
                  <td>{pedido.direccion}</td>
                  <td>{pedido.metodo}</td>
                  <td>{pedido.codigo}</td>
                  <td>{pedido.total}</td>
                  <td>{pedido.estado}</td>
                </tr>
              ))}
            </tbody>
          </table>
        }
      </div>
    );
  };
  
  export default EstadoPedido;
