import TopNav from '../Global/TopNav';
import { useEffect, useState } from 'react';
import axios from 'axios';

const Recomendaciones = ({ userID }) => {
  const [recomendaciones, setRecomendaciones] = useState([]);

  useEffect(() => {
    axios
      .get(`http://localhost:8000/endpoints/recomendaciones?userID=${userID}`)
      .then((response) => {
        setRecomendaciones(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [userID]);

  return (
    <div>
      <TopNav />
      <h2>Recomendaciones para el usuario {userID}</h2>
      <ul>
        {recomendaciones.length > 0 ? (
          recomendaciones.map((recomendacion) => (
            <li key={recomendacion.id}>{recomendacion.title}</li>
          ))
        ) : (
          <li>No se encontraron recomendaciones</li>
        )}
      </ul>
    </div>
  );
};

export default Recomendaciones;

